
#importing libraries
import numpy as np 
import matplotlib.pyplot as plt  
import smtplib
from jinja2 import Template
import base64
from io import BytesIO
import email.message


# create message object instance
msg = email.message.Message()
message = "Thank you"

# setup the parameters of the message
#from email address
msg['From'] = "xxxxx.xxxxx@gmail.com"
#from email password
password = "xxxxxxx"
#To email address
msg['To'] = "xxxxx@xxxxxx.com"
#Email subject
msg['Subject'] = "Email trigger - Atttach images/matplotlib figures without saving locally in python"
 

#converting matplotlib figure to Base64-encoded string
plt.figure()
x = np.arange(0, 3 * np.pi, 0.1)
y = np.tan(x) 
plt.title("tan wave form") 
plt.plot(x, y)
#creating im-memory buffer to store data in bytes
figdata = BytesIO()
#saving image in buffer
plt.savefig(figdata, format='png')
#converting image to base64-encoded string
tan_plot = base64.b64encode(figdata.getvalue()).decode("utf-8")
figdata.flush()
figdata.close()
plt.close()

#base64-encoded string of a line plot
line_plot = ""
#base64-encoded string of a car image
car_image = ""

#variable data to add in email body
title = "Attach matplotlib figures/any image file to an e-mail and send without saving locally in python"

#building email body template
main = Template('''
<html>
    <head></head>
    <body>
         <p>Problem statement : <font color="red">{{title}}</font></p>
         <p>Tangent function - plot <br><img src="data:image/jpeg;base64,{{tan_plot}}" /></p>
         <p>Line plot<br><img src="data:image/jpeg;base64,{{line_plot}}" /></p>
         <p>Car Image <br><img src="data:image/jpeg;base64,{{car_image}}" /></p>
    </body>
</html>
''')

msg.add_header('Content-Type', 'text/html')
#rendering the variables
html = main.render(title = title, car_image = car_image, line_plot = line_plot, tan_plot = tan_plot)
msg.set_payload(html)

#creating server
server = smtplib.SMTP('smtp.gmail.com: 587')
server.starttls()
# Login Credentials for sending the mail
server.login(msg['From'], password)
# sending the message via the server.
server.sendmail(msg['From'], msg['To'], msg.as_string())
server.quit()
print ("successfully sent email to %s:" % (msg['To']))
